package arraytask;

public class sumarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int[]num= {2,4,6,8,10,44};
  int sum=0;
  for(int i:num)
	sum+=i;
	System.out.println(" Total Value is:"+sum);
	
	for(int i=0;i<=10;i++) {
		for (int j=0;j<=10;j++) {
			System.out.print("-");
		}
		System.out.print("\n");
	}
	
	
	
	
	}

}
